import type { FetchOptions } from "./FetchOptions";
/**
 * @module interfaces
 */
/**
 * Additional options for saving a dataset/resource in a container. Extends
 * FetchOptions and includes:
 * - `slugSuggestion`: A user suggested resource/container name. If a container
 *   does not already have a resource/container in it with that name, it will
 *   use the slug suggestion as the name.
 *
 * @since 2.1.0
 */
export interface SaveInContainerOptions extends FetchOptions {
    slugSuggestion?: string;
}
